DROP TABLE IF EXISTS incidents;

CREATE TABLE incidents
(
    id            SERIAL PRIMARY KEY,
    title         VARCHAR NOT NULL,
    description   TEXT,
    incident_type VARCHAR,
    location      VARCHAR,
    image         VARCHAR,
    incident_date DATE,
    created_at    TIMESTAMP,
    updated_at    TIMESTAMP
);

